import React, { Component } from 'react';
import {Card, Modal, Button, Row, Col} from 'react-bootstrap';
import { PencilFill, Trash } from 'react-bootstrap-icons';
import './Quote.css';

class QuoteCard extends Component {

    constructor(props) {
        super(props);
        this.state = {
          editShow: false,
          deleteShow: false
        };
        this.handleEditClose = this.handleEditClose.bind(this);
        this.handleEditShow = this.handleEditShow.bind(this);
        this.handleDeleteClose = this.handleDeleteClose.bind(this);
        this.handleDeleteShow = this.handleDeleteShow.bind(this);
    }

    handleEditClose = () => {
        this.setState({
            editShow : false
        })
    }

    handleEditShow = () => {
        this.setState({
            editShow : true
        })
    }

    handleDeleteClose = () => {
        this.setState({
            deleteShow : false
        })
    }

    handleDeleteShow = () => {
        this.setState({
            deleteShow : true
        })
    }


  render() {
    const { editShow, deleteShow } = this.state;

    return (
        <div className="App">
            <Card>
                <Card.Header>
                <Row>
                    <Col md={10} className="float-left">Quote</Col>
                    <Col md={1}> <PencilFill onClick={this.handleEditShow}/></Col>
                    <Col md={1}><Trash onClick={this.handleDeleteShow}/></Col>

                </Row>
                </Card.Header>
                <Card.Body>
                    <blockquote className="blockquote mb-0">
                        <p>
                        {' '}
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere
                        erat a ante.{' '}
                        </p>
                        <footer className="blockquote-footer">
                        Someone famous in <cite title="Source Title">Source Title</cite>
                        </footer>
                    </blockquote>
                </Card.Body>
            </Card>

            {/*edit modal*/}
            <Modal show={editShow} onHide={this.handleEditClose}>
                <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={this.handleEditClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={this.handleEditClose}>
                    Save Changes
                </Button>
                </Modal.Footer>
            </Modal>

            {/*delete modal*/}
            <Modal show={deleteShow} onHide={this.handleDeleteClose}>
                <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={this.handleDeleteClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={this.handleDeleteClose}>
                    Save Changes
                </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
  }
}

export default QuoteCard;
